import React, { useState } from "react";
import Joi from "joi";

function AdminPage() {
  const [formData, setFormData] = useState({
    dataType: "event",
    title: "",
    description: "",
    image: "",
  });

  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Joi validation schema
  const schema = Joi.object({
    dataType: Joi.string().valid("event", "partner", "programming").required(),
    title: Joi.string().min(3).max(100).required(),
    description: Joi.string().min(10).max(500).required(),
    image: Joi.string().uri().allow(""), // Optional image URL
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formattedData = {
        ...formData,
        details: formData.details.split(",").map((item) => item.trim()),
        attendees: parseInt(formData.attendees, 10),
    };

    fetch("http://localhost:3000/api/events", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formattedData),
    })
        .then((res) => {
            if (!res.ok) {
                throw new Error("Network response was not ok");
            }
            return res.json(); // Ensure the response is JSON
        })
        .then((data) => {
            if (data.success) {
                setSuccess("Event added successfully!");
                fetchEvents(); // Reload the event list
            } else {
                setError(data.message);
            }
        })
        .catch((err) => {
            console.error("Error:", err);
            setError("An unexpected error occurred.");
        });
};


  return (
    <div className="admin-panel">
      <h1>Admin Panel</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="dataType">Select Data Type:</label>
          <select name="dataType" id="dataType" value={formData.dataType} onChange={handleChange}>
            <option value="event">Event</option>
            <option value="partner">Partner</option>
            <option value="programming">Programming</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="Enter Title"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Enter Description"
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="image">Image URL:</label>
          <input
            type="url"
            id="image"
            name="image"
            value={formData.image}
            onChange={handleChange}
            placeholder="Enter Image URL"
          />
        </div>
        <button type="submit">Submit</button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}
    </div>
  );
}

export default AdminPage;
