import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import "../style.css";

function TimelinePage() {
  const [events, setEvents] = useState([]);

  // Fetch events from the backend
  const fetchEvents = () => {
    fetch("https://nsbe-react-website-backend.onrender.com/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch events");
        }
        return response.json(); // Parse the JSON response
      })
      .then((data) => setEvents(data)) // Update state with fetched events
      .catch((error) => console.error("Error fetching events:", error));
  };

  // Use effect to load events when the component loads
  useEffect(() => {
    fetchEvents();
  }, []);

  return (
    <div>
      <section className="timeline-section">
        <h1>50 Years of NSBE</h1>
        <div id="timeline-container">
          {events.length > 0 ? (
            events.map((event) => (
              <div key={event._id} className="timeline-item">
                <div className="timeline-date">{event.date}</div>
                <div className="timeline-content">
                  <img
                    src={`images/${event.img_name}`}
                    alt={event.event}
                    className="event-image"
                  />
                  <h3>{event.event}</h3>
                  <p>{event.description}</p>
                  <ul>
                    {event.details.map((detail, index) => (
                      <li key={index}>{detail}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))
          ) : (
            <p>Loading timeline events...</p>
          )}
        </div>
      </section>
    </div>
  );
}

export default TimelinePage;
